analyze = (url) => {
  var a;
  a = document.getElementById('pgn');
  a.hidden = false;
  a.value = global.chess.pgn();
  a.select();
  document.execCommand('copy');
  a.hidden = true;
  return window.open(url, "_blank");
};

newGame = () => {
  var seconds;
  global.chess.reset();
  seconds = global.minutes * 60 + global.increment;
  return global.clocks = [seconds, seconds];
};

